        static public bool Analisis(List<Token> tokens)
        {
            int i, j, tam, res;
            Par tope; 

            Stack <Par> pila = new Stack<Par>();
            pila.Push(new Par(0, 0)); //La pila empieza con estado 0

            tam = tokens.Count;
            i = 0; //Recorre la lista de tokens

            while (i < tam)
            {
                tope = pila.Peek();

                res = tablaSRL[tope.estado, tokens[i].identificador];

                if (res == 0) //Error
                    return false;
                else if (res == -1) //Aceptacion
                    return true;
                else if (res > 0)
                {
                    pila.Push(new Par(res, tokens[i++].identificador));   //Desplazamiento, guardamos el estado y el identificador del token e incrementamos i
                }
                else
                {
                    res *= -1; //Lo volvemos positivo para usarlo en el arreglo de reglas
                    for (j = 0; j < idReglas[res, 1]; j++) //Hacemos pop el numero de veces que indica nuestro arreglo de reglas
                        pila.Pop();

                    tope = pila.Peek(); //Necesitamos saber el estado actual de la pila para agregar el estado de la regla usada

                    pila.Push(new Par(tablaSRL[tope.estado, idReglas[res, 0]], idReglas[res, 0]));
                }
            }
            return false;
        }

    }
